import React, { Component } from 'react';
import axios from 'axios';
import { Modal, Button, Form, FormControl } from 'react-bootstrap'
import config from "../config.json";
import ErrorPage from '../error-views/errorPage';
import ReactFormValidation from "react-form-input-validation";
import '../App.css';
class Showusers extends Component {
  constructor(props) {
    
    super(props);
   
     this.state = {
      optionRadio:["3 Months","6 Months", "1 Year"],
      showModal: false,
      showPaymentModal:false,
      userData: [],
      sendData:{},
      apiError:null,
      token: localStorage.getItem("token"),
      branchCode:this.props.match.params.branch,
      weekDay: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
      fields: {
        sNo:"",
        userName:"",
        mobileNumber:"",
        joiningDate:"",
        status: "",
        branchCode: "",
        subscription: "None",
        amount:""
      },
      errors: {}
    };
    
  console.log(this.state.sendData);
    this.form = new ReactFormValidation(this, { locale: "en" });
    this.form.useRules({
      userName: "required",
      mobileNumber: "required|numeric",
      branchCode: "required",
      amount: "required|numeric",
      status:"required",
      subscription:"required"
    });
  }
  

 d = new Date();
 month = ("0"+ (this.d.getMonth()+1)).slice(-2);
 date = ("0"+ this.d.getDate()).slice(-2);
 day = this.d.getDay();
 dformat = [this.day,this.month, this.d.getFullYear(), this.date];
  
  componentDidMount() {
 this.getDetails();
  }
  async  getDetails(){
    console.log(this.state.branchCode)
    let url=config.userDetails+"?branch="+this.state.branchCode;
    let userData =[];
     await axios
    .get(url, { headers: { 'Authorization': this.state.token } })
  .then(resp=>{
    if(resp.data.status=="error") this.setState({apiError:resp.data});
    else  userData =resp.data;
  })
  .catch(error => {
    this.setState({apiError:error.response});
  });
 
  if(userData=="Add user"){
    alert("Add user of this branch");
    this.props.history.push(`/admin/home`);
  }
  this.setState({userData:userData,sendData:userData[0]});
  console.log(this.state.userData);
  }

  async postData(fields) {
    console.log(fields);
    const url= config.userPayment;
    await axios.put(url, fields,{headers:{'Authorization':this.state.token}}).then(
        (response) => {
          if(response.data.status=="error") this.setState({apiError:response.data});
          else
          this.setState({showPaymentModal:"false"});
        } )
        .catch(error => {
          this.setState({apiError:error.response});
        });
    
  }
  handleCloseShow() {
    this.setState({ showModal: false });
  }
  handleSubmitShow() {
    this.setState({ showModal: false });
  }
  handleClosePayment() {
    this.setState({ showPaymentModal: false });
  }
  async handleSubmitPayment() {
    console.log("data",this.state.fields.amount)
   if(this.state.fields.amount==""){
     alert("Emter Amount");
     return false;
   }
   else{
    console.log("payment",this.state.fields);
    const url= config.userPayment;
    await axios.put(url, this.state.fields,{headers:{'Authorization':this.state.token}}).then(
        (response) => {
          if(response.data.status=="error") this.setState({apiError:response.data});
          else
          this.setState({showPaymentModal:"false"});
        }
      )
      .catch(error => {
        this.setState({apiError:error.response});
      });
    this.setState({ showPaymentModal: false });
   }
  }
  paymentSubmit(data){
    this.setState({ showPaymentModal: true});
    let obj={
      sNo:data.sNo,
      userName: data.userName,
      mobileNumber: data.mobileNumber,
      branchCode: data.branchCode,
      amount: "",
      joiningDate: data.joiningDate,
      status:data.status,
      subscription:"None"
    }
    this.setState({fields:obj});
  }
  showData(data) {
    this.setState({ showModal: true,sendData:data });
  }
async  editData(data) {
    await this.props.history.push({ 
      pathname: '/admin/editData',
      state: data
     });
  }
 async Filteration(event) {
   let value=event.target.value;
   await  this.getDetails();
  if(value!="All"){
    let data = this.state.userData.filter(obj=>obj.paymentDate==value);
    console.log(data);
    if(data.length!=0)this.setState({userData:data});
    else alert("Data not find");
  }
  }
handleSearchInput =async event => {
 let value= event.target.value;
 await  this.getDetails();
 let data = this.state.userData.filter(obj=>{
let len=obj.userName.match(value);
if(len) return obj;
});
console.log(data);
if(data.length!=0)this.setState({userData:data});
else alert("User name not exist");
  };
 
  render() {
    return (
      <React.Fragment>
          {this.state.apiError?<ErrorPage details={this.state.apiError}/>:<>
      {this.state.userData.length==0 ? (
       <div className="d-flex justify-content-center mt-5">
       <h3> Loading</h3><div className="spinner-border" style={{width: "3rem", height: "3rem", color:"green"}} role="status">
   
   </div>
   </div>
      ) : (
   <div style={{ backgroundColor: "whitesmoke" }} className="container-fluid">
           <div style={{ padding: 5, marginBottom: 5 }}>
          <div className="row m-4">
            <div className="col-6">
           <FormControl
              onChange={this.handleSearchInput}
              value={this.state.searchText}
              type="text"
              placeholder="Search by name"
            />
             </div>
            <div className="col-6">
            <select className="form-select" aria-label="Default select example"
             onChange={e=>this.Filteration(e)}>
              <option value="All">All</option>
              <option value="1">Three day due date</option>
              <option value="2">Two day due date</option>
              <option value="3">Ond day due date</option>
            </select>
              </div>
          </div>
           
       
          
         
  <div className="table-responsive-sm">
 <table className="table">
  <thead>
    <tr>
      <th scope="col">User Name</th>
      <th scope="col">Mobile Number</th>
      <th scope="col">Joining Date</th>
      <th scope="col">Expiry Date</th>
      <th scope="col">Subscription</th>
      <th scope="col">Amount</th>
      <th scope="col">Payment</th>
      <th scope="col">Status</th>
      <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
