import React, { Component } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap'
import config from "../config.json";
import ReactFormValidation from "react-form-input-validation";
class Showusers extends Component {
  constructor(props) {
    
    super(props);
   
     this.state = {
      editModal: false,
      userData: [],
     sendData:{ sNo:"1",
      userName:"",
      mobileNumber:"",
      joiningDate:"",
      paidDate:"",
      dueDate:"",
      status: "",
      branchCode: "",
      subscription: "",
      amount:""},
      weekDay: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
      details:this.props.location.state,
    fields: {
      sNo:this.state.sendData.sNo,
      userName:this.sendData.userName,
      mobileNumber:this.sendData.mobileNumber,
      joiningDate:"",
      paidDate:"",
      dueDate:"",
      status: "",
      branchCode: "",
      subscription: "",
      amount:""
    },
    errors: {}
  };
console.log(this.state.sendData);
  this.form = new ReactFormValidation(this, { locale: "en" });
  this.form.useRules({
    userName: "required",
    mobileNumber: "required|numeric|digits:10",
    branchCode: "required",
    amount: "required|numeric",
    paidDate:"required",
    status:"required"
  });
  
  this.form.onformsubmit = (fields) => {
    //this.postData(fields);
    console.log(fields);
  }
}

 d = new Date();
 month = ("0"+ (this.d.getMonth()+1)).slice(-2);
 date = ("0"+ this.d.getDate()).slice(-2);
 day = this.d.getDay();
  dformat = [this.day,this.month, this.d.getFullYear(), this.date];
  
  componentDidMount() {
 this.getDetails();
  }
  async  getDetails(){
    let url=config.userDetails+"?branch="+this.state.details.branchCode;
    const userData = await axios
    .get(url, { headers: { 'Authorization': this.state.token } })
  .then(resp=>resp.data)
  .catch(error => {
    if(error.response.status===403){
      alert("Your session has expired! Please Login again!")
      this.props.history.push(`/rmPortal`);
    }
  });
  this.setState({userData:userData,sendData:userData[0]});
  }

  async postData(fields) {
    // let obj={
    //   sNo:fields.sNo,
    //   userName:fields.userName,
    //   mobileNumber: fields.number,
    //   joiningDate:fields.joiningDate,
    //   paidDate:fields.paidDate,
    //   dueDate:fields.dueDate,
    //   status: fields.status,
    //   branchCode: fields.branchCode,
    //   subscription: fields.subscription,
    //   amount: fields.amount,
    // }
    console.log();
    // const url= config.userEdit;
    // await axios.post(url, obj).then(
    //     (response) => {
    //       console.log(response);
    //     },
    //     (error) => {
    //       console.log(error);
    //     }
    //   );
    
  }
  handleCloseEdit() {
    this.setState({ editModal: false });
  }
  handleSubmitEdit() {

    this.setState({ editModal: false });
  }
  
  editData(data) {
    // this.sendData= data;
    // console.log(this.sendData);
    this.setState({ editModal: true,sendData:data });
  //  this.state.fields=data;
 //this.setState({fields: this.state.fields});
  }
  render() {
    return (
      <div style={{ backgroundColor: "#F8C47180" }} className="container-fluid">
           <div style={{ padding: 5, marginBottom: 5 }}>
                  <h5 className="text-center">User Details</h5>
              <div className="row ">
                  <div className="col-10 offset-1 bg-light ">
                  <div className="row border ">
                    <div className="col border">User Name</div>
                    <div className="col border">Mobile Number</div>
                    <div className="col border">Joining Date</div>
                    <div className="col border">Paid Date</div>
                    <div className="col border">Due Date</div>
                   <div className="col border">Subscription</div> 
                   <div className="col border">Amount</div> 
                    <div className="col border">Status</div>
                    <div className="col border">Action</div>
                </div>
                {this.state.userData.map((data, index) => (   
               <div className="row border" key={index}>
                    <div className="col border">{data.userName}</div>
                    <div className="col border">{data.mobileNumber}</div>
                    <div className="col border">{data.joiningDate}</div>
                    <div className="col border">{data.paidDate}</div>
                    <div className="col border">{data.dueDate}</div>
                    <div className="col border">{data.subscription}</div>
                    <div className="col border">{data.amount}</div>
                    <div className="col border">{data.status}</div>
                    <div className="col border">
                       <Button className="btn btn-primary" onClick={() => this.editData(data)}>Edit</Button>
                       </div>
                </div>
               ))}
                  </div>
              </div>
                </div>
                <Modal show={this.state.editModal} onHide={() => this.handleCloseEdit()} size="lg">
          <Modal.Header closeButton>
            <Modal.Title>Edit Details</Modal.Title>
          </Modal.Header>
          <Modal.Body>

            <Form className="myForm" noValidate onSubmit={this.form.handleSubmit} >
              <Form.Group>
                <Form.Label> Branch Code <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="branchCode"
                 
                  onBlur={this.form.handleBlurEvent}
                  onChange={this.form.handleChangeEvent}
                  value={this.state.fields.branchCode}
                  // To override the attribute name
                  data-attribute-name="" />
                <div className="alert-danger text-danger">
                  {this.state.errors.branchCode
                    ? this.state.errors.branchCode
                    : ""}
                </div>
              </Form.Group>
              <Form.Group>
                <Form.Label> User Name <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="userName"
                  onBlur={this.form.handleBlurEvent}
                  onChange={this.form.handleChangeEvent}
                  value={this.state.fields.userName}
                  // To override the attribute name
                  data-attribute-name="" />
                <div className="alert-danger text-danger">
                  {this.state.errors.userName
                    ? this.state.errors.userName
                    : ""}
                </div>
              </Form.Group>

              <Form.Group>
                <Form.Label> Mobile Number <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="mobileNumber"
                  onBlur={this.form.handleBlurEvent}
                  onChange={this.form.handleChangeEvent}
                  value={this.state.fields.mobileNumber}
                  // To override the attribute name
                  data-attribute-name="" />
                <div className="alert-danger text-danger">
                  {this.state.errors.mobileNumber
                    ? this.state.errors.mobileNumber
                    : ""}
                </div>
              </Form.Group>

              <Form.Group>
                <Form.Label> Paid Date <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="paidDate"
                  onBlur={this.form.handleBlurEvent}
                  onChange={this.form.handleChangeEvent}
                  value={this.state.fields.paidDate}
                 
                  data-attribute-name="" />
                <div className="alert-danger text-danger">
                  {this.state.errors.paidDate
                    ? this.state.errors.paidDate
                    : ""}
                </div>
              </Form.Group>

              <Form.Group>
                <Form.Label> Status <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="status"
                  onBlur={this.form.handleBlurEvent}
                  onChange={this.form.handleChangeEvent}
                  value={this.state.fields.status}
                 
                  data-attribute-name="" />
                <div className="alert-danger text-danger">
                  {this.state.errors.status
                    ? this.state.errors.status
                    : ""}
                </div>
              </Form.Group>
              
              <Form.Group>
                <Form.Label> Amount <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="amount"
                  onBlur={this.form.handleBlurEvent}
                  onChange={this.form.handleChangeEvent}
                  value={this.state.fields.amount}
                 
                  data-attribute-name="" />
                <div className="alert-danger text-danger">
                  {this.state.errors.amount
                    ? this.state.errors.amount
                    : ""}
                </div>
              </Form.Group>
              
              <button type="submit" className="btn btn-primary mt-3">Submit</button>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={() => this.handleSubmitEdit()}>
              Ok
            </Button>
          </Modal.Footer>
        </Modal>

        </div>
      
    );
  }
}
export default Showusers;
