import React from "react";
import ReactFormValidation from "react-form-input-validation";
import Form  from 'react-bootstrap/Form';
import axios from 'axios';
import config from "../config.json";
import '../App.css';
class Loginpage extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
        token:"",
      fields: {
        userName: "",
        password:"",
      
      },
      errors: {}
    };
    this.form = new ReactFormValidation(this, { locale: "en" });
    this.form.useRules({
      userName:"required",
      password:"required",
     
     });

    this.form.onformsubmit = (fields) => {
     this.postData(fields);
     console.log(fields);
    }
  }
  async postData(fields){
    const url= config.login;
     let obj = {
      userName: fields.userName,
      password : fields.password
         };
console.log(obj);
    await axios.post(url, obj).then(
      (response) => {
        console.log(response);
        if(response.data=="wrong credential") alert("enter correct name and password");
        // else  if(response.data.role!="Manager") alert("enter correct name and password");
       else {
         localStorage.setItem("branch","login" );
         localStorage.setItem("branchCode",obj.userName);
         window.location="/branch/home/"+obj.userName;
        // this.props.history.push("/branch/home");
       }
      },
      (error) => {
        console.log(error);
      }
    );
  }
    render() { 
        return (
          <div  className="container-fluid">
            <div className="row ">
              
              <div className="card col-8  login-card  offset-2 my-5 bg">
                  <div className="row " style={{margin:1}}>
                 
                  <div  className="col-12 font-weight-bold text-center mt-4">
                  <h1 className="letterFormate">Branch Login </h1></div>
                  </div>
                    
                  <div className="row  my-3">
                 <div className=" card col-6 offset-3 bg login-card">
                 <Form className="myForm" noValidate onSubmit={this.form.handleSubmit} >
                  <Form.Group className="my-4">
                  <Form.Label> User Name <i className="text-danger">*</i> </Form.Label>
                 <Form.Control
                  type="text"
                  name="userName"
                 style={{backgroundColor:"#f2f2f2"}}
                  onBlur={this.form.handleBlurEvent}
                  onChange={this.form.handleChangeEvent}
                  value={this.state.fields.userName}
                  // To override the attribute name
                  data-attribute-name="" />
                  
                  <div className="alert-danger text-danger">
                  {this.state.errors.userName
                  ? this.state.errors.userName
                  : ""}
                  </div>
                  </Form.Group>
                  <Form.Group className="mt-4">
                  <Form.Label>Password <i className="text-danger">*</i> </Form.Label>
                 <Form.Control
                  type="text"
                  name="password"
                  style={{backgroundColor:"#f2f2f2"}}
                  onBlur={this.form.handleBlurEvent}
                  onChange={this.form.handleChangeEvent}
                  value={this.state.fields.password}
                  // To override the attribute name
                  data-attribute-name="" />
                  <div className="alert-danger text-danger">
                  {this.state.errors.password
                  ? this.state.errors.password
                  : ""}
                  </div>
                  </Form.Group>
                
                  <button type="submit" className="btn my-4 text-light text-bold" style={{backgroundColor:"#fa7a23"}}>Login</button>
                  </Form>
                 </div>
              </div>
              </div>
            </div>
          </div>
          
         );
    }
}

export default Loginpage;
