import React, { Component } from 'react';
import axios from 'axios';
import { Modal, Button, Form, FormControl } from 'react-bootstrap'
import config from "../config.json";
import Loading from '../Loading/loadingRound'
import ReactFormValidation from "react-form-input-validation";
import '../App.css';
class Homepage extends Component {
  constructor(props) {
    
    super(props);
   
     this.state = {
      showModal: false,
      showModalPayment:false,
      userData: [],
      sendData:{},
      branchCode:this.props.match.params.branch,
      weekDay: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
      details:{},
      fields: {
        sNo:"",
        userName:"",
        mobileNumber:"",
        joiningDate:"",
        paidDate:"",
        dueDate:"",
        status: "",
        branchCode: "",
        subscription: "",
        amount:""
      },
      errors: {}
    };
    
  console.log(this.state.sendData);
    this.form = new ReactFormValidation(this, { locale: "en" });
    this.form.useRules({
      userName: "required",
      mobileNumber: "required|numeric",
      branchCode: "required",
      amount: "required|numeric",
      paidDate:"required",
      status:"required"
    });
    
    this.form.onformsubmit = (fields) => {
      this.postData(fields);
      console.log(fields);
    }
  }
  

 d = new Date();
 month = ("0"+ (this.d.getMonth()+1)).slice(-2);
 date = ("0"+ this.d.getDate()).slice(-2);
 day = this.d.getDay();
 dformat = [this.day,this.month, this.d.getFullYear(), this.date];
  
  componentDidMount() {
 this.getDetails();
  }
  async  getDetails(){
    console.log(this.state.branchCode)
    let url=config.userDetails+"?branch="+this.state.branchCode;
    const userData = await axios
    .get(url, { headers: { 'Authorization': this.state.token } })
  .then(resp=>resp.data)
  .catch(error => {
    if(error.response.status===403){
      alert("Your session has expired! Please Login again!")
      this.props.history.push(`/branch`);
    }
  });
localStorage.setItem("branchCode",this.state.branchCode);
  this.setState({userData:userData,sendData:userData[0]});
  console.log(this.state.userData);
  }

  async postData(sendData) {
    // let obj={
    //   sNo:sendData.sNo,
    //   userName:sendData.userName,
    //   mobileNumber: sendData.number,
    //   joiningDate:sendData.joiningDate,
    //   paidDate:sendData.paidDate,
    //   dueDate:sendData.dueDate,
    //   status: sendData.status,
    //   branchCode: sendData.branchCode,
    //   subscription: sendData.subscription,
    //   amount: sendData.amount,
    // }
    console.log();
    // const url= config.userEdit;
    // await axios.post(url, obj).then(
    //     (response) => {
    //       console.log(response);
    //     },
    //     (error) => {
    //       console.log(error);
    //     }
    //   );
    
  }
  handleCloseShow() {
    this.setState({ showModal: false });
  }
  handleSubmitShow() {
    this.setState({ showModal: false });
  }
  handleClosePayment() {
    this.setState({ showModal: false });
  }
  handleSubmitPayment() {
    this.setState({ showModal: false });
  }
  paymentSubmit(data){
    this.setState({ showModalPayment: true,details:data });
    this.setState({fields:data});
  }
  showData(data) {
    this.setState({ showModal: true,sendData:data });
  }
async  editData(data) {
    await this.props.history.push({ 
      pathname: '/branch/editData',
      state: data
     });
  }
 async Filteration(event) {
   let value=event.target.value;
   await  this.getDetails();
  if(value!="All"){
    let data = this.state.userData.filter(obj=>obj.paymentDate==value);
    console.log(data);
    if(data.length!=0)this.setState({userData:data});
    else alert("Data not find");
  }
  }
handleSearchInput =async event => {
 let value= event.target.value;
 await  this.getDetails();
 let data = this.state.userData.filter(obj=>{
let len=obj.userName.match(value);
if(len) return obj;
});
console.log(data);
if(data.length!=0)this.setState({userData:data});
else alert("User name not exist");
  };
 
  render() {
    return (
      <React.Fragment>
      {this.state.userData.length==0 ? (
      <h4>  Loading</h4>
