import React, { Component } from 'react';
import axios from 'axios';
import { Modal, Button, Form, FormControl } from 'react-bootstrap'
import config from "../config.json";
import Loading from '../Loading/loadingRound'
import ReactFormValidation from "react-form-input-validation";
import '../App.css';
class Homepage extends Component {
  constructor(props) {
    
    super(props);
   
     this.state = {
      optionRadio:["3 Months","6 Months", "1 Year"],
      showModal: false,
      showPaymentModal:false,
      userData: [],
      sendData:{},
      branchCode:this.props.match.params.branch,
      weekDay: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
      fields: {
        sNo:"",
        userName:"",
        mobileNumber:"",
        joiningDate:"",
        status: "",
        branchCode: "",
        subscription: "None",
        amount:""
      },
      errors: {}
    };
    
  console.log(this.state.sendData);
    this.form = new ReactFormValidation(this, { locale: "en" });
    this.form.useRules({
      userName: "required",
      mobileNumber: "required|numeric",
      branchCode: "required",
      amount: "required|numeric",
      status:"required",
      subscription:"required"
    });
  }
  

 d = new Date();
 month = ("0"+ (this.d.getMonth()+1)).slice(-2);
 date = ("0"+ this.d.getDate()).slice(-2);
 day = this.d.getDay();
 dformat = [this.day,this.month, this.d.getFullYear(), this.date];
  
  componentDidMount() {
 this.getDetails();
  }
  async  getDetails(){
    console.log(this.state.branchCode)
    let url=config.userDetails+"?branch="+this.state.branchCode;
    const userData = await axios
    .get(url, { headers: { 'Authorization': this.state.token } })
  .then(resp=>resp.data)
  .catch(error => {
    if(error.response.status===403){
      alert("Your session has expired! Please Login again!")
      this.props.history.push(`/branch`);
    }
  });
localStorage.setItem("branchCode",userData);
  this.setState({userData:userData,sendData:userData[0]});
  console.log(this.state.userData);
  }

  async postData(fields) {
    console.log(fields);
    const url= config.userPayment;
    await axios.put(url, fields).then(
        (response) => {
          console.log(response);
          this.setState({showPaymentModal:"false"});
        },
        (error) => {
          console.log(error);
        }
      );
    
  }
  handleCloseShow() {
    this.setState({ showModal: false });
  }
  handleSubmitShow() {
    this.setState({ showModal: false });
  }
  handleClosePayment() {
    this.setState({ showPaymentModal: false });
  }
  async handleSubmitPayment() {
    console.log("data",this.state.fields.amount)
   if(this.state.fields.amount==""){
     alert("Enter Amount");
     return false;
   }
   else{
    console.log(this.state.fields);
    const url= config.userPayment;
    await axios.put(url, this.state.fields).then(
        (response) => {
          console.log(response);
          this.setState({showPaymentModal:"false"});
        },
        (error) => {
          console.log(error);
        }
      );
    
    this.setState({ showPaymentModal: false });
   }
  }
  paymentSubmit(data){
    this.setState({ showPaymentModal: true});
    let obj={
      sNo:data.sNo,
      userName: data.userName,
      mobileNumber: data.mobileNumber,
      branchCode: data.branchCode,
      amount: "",
      joiningDate: data.joiningDate,
      status:data.status,
      subscription:"None"
    }
    this.setState({fields:obj});
  }
  showData(data) {
    this.setState({ showModal: true,sendData:data });
  }
async  editData(data) {
    await this.props.history.push({ 
      pathname: '/branch/editData',
      state: data
     });
  }
 async Filteration(event) {
   let value=event.target.value;
   await  this.getDetails();
  if(value!="All"){
    let data = this.state.userData.filter(obj=>obj.paymentDate==value);
    console.log(data);
    if(data.length!=0)this.setState({userData:data});
    else alert("Data not find");
  }
  }
handleSearchInput =async event => {
 let value= event.target.value;
 await  this.getDetails();
 let data = this.state.userData.filter(obj=>{
let len=obj.userName.match(value);
if(len) return obj;
});
console.log(data);
if(data.length!=0)this.setState({userData:data});
else alert("User name not exist");
  };
 
  render() {
    return (
      <React.Fragment>
      {this.state.userData.length==0 ? (
     <div class="d-flex justify-content-center mt-5">
       <div class="spinner-border" style={{width: "3rem", height: "3rem", color:"green"}} role="status">
     <span class="sr-only text-warning ">Loading...</span>
   </div>
   {/* <div class="spinner-grow" style={{width: "3rem", height: "3rem"}} role="status">
     <span class="sr-only">Loading...</span>
   </div> */}
   </div>
      ) : (
   <div style={{ backgroundColor: "whitesmoke" }} className="container-fluid">
           <div style={{ padding: 5, marginBottom: 5 }}>
           <div className="row m-5">
           <form onSubmit={this.handleSubmit} className="form-control" >
       <label>  Write message:</label>
          <input value={this.state.value} onChange={this.handleChange} style={{width: "80%"}}/>
      
        <input type="submit" value="Send to all" className=" ml-2 btn btn-primary btn-sm"/>
      </form>
      </div>
          <div className="row m-4">
            <div className="col-6">
           <FormControl
              onChange={this.handleSearchInput}
              value={this.state.searchText}
              type="text"
              placeholder="Search by name"
            />
             </div>
            <div className="col-6">
            <select className="form-select" aria-label="Default select example"
             onChange={e=>this.Filteration(e)}>
              <option value="All">All</option>
              <option value="1">Three day due date</option>
              <option value="2">Two day due date</option>
              <option value="3">Ond day due date</option>
            </select>
              </div>
          </div>
           
       
          
         
  <div className="table-responsive-sm">
  <table className="table">
  <thead>
    <tr>
      <th scope="col">User Name</th>
      <th scope="col">Mobile Number</th>
      <th scope="col">Joining Date</th>
      <th scope="col">Expiry Date</th>
      <th scope="col">Subscription</th>
      <th scope="col">Amount</th>
      <th scope="col">Payment</th>
      <th scope="col">Status</th>
      <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
   {this.state.userData.map((data, index) => (   
  data.paymentDate<=1&&data.status=="Active"?
   <tr class="table-danger">
      <th  scope="row" className="text-primary" onClick={() => this.showData(data)}>{data.userName}</th>
      <td>{data.mobileNumber}</td>
      <td>{data.joiningDate}</td>
      <td>{data.dueDate}</td>
      
      <td>{data.subscription}</td>
       <td>{data.amount}</td> 
       <td>   
         <Button className="btn btn-primary" onClick={() => this.paymentSubmit(data)}>Payment</Button></td>
      <td>{data.status}</td>
      <td>   <Button className="btn btn-primary" onClick={() => this.editData(data)}>Edit</Button></td>
    </tr>: 
    data.paymentDate==2&&data.status=="Active"?
    <tr class="table-warning">
      <th  scope="row" className="text-primary" onClick={() => this.showData(data)}>{data.userName}</th>
      <td>{data.mobileNumber}</td>
      <td>{data.joiningDate}</td>
      <td>{data.dueDate}</td>
      <td>{data.subscription}</td>
       <td>{data.amount}</td> 
       <td>   <Button className="btn btn-primary" onClick={() => this.paymentSubmit(data)}>Payment</Button></td>
      <td>{data.status}</td>
      <td>   <Button className="btn btn-primary" onClick={() => this.editData(data)}>Edit</Button></td>
    </tr>:  data.paymentDate==3&&data.status=="Active"?
   <tr className="table-success">
   <th  scope="row" className="text-primary" onClick={() => this.showData(data)}>{data.userName}</th>
   <td>{data.mobileNumber}</td>
   <td>{data.joiningDate}</td>
   <td>{data.dueDate}</td>
   <td>{data.subscription}</td>
    <td>{data.amount}</td> 
    <td>   <Button className="btn btn-primary" onClick={() => this.paymentSubmit(data)}>Payment</Button></td>
   <td>{data.status}</td>
   <td>   <Button className="btn btn-primary" onClick={() => this.editData(data)}>Edit</Button></td>
 </tr>
 :data.status=="Inactive"?
   <tr className="table-dark">
   <th  scope="row" className="text-primary" onClick={() => this.showData(data)}>{data.userName}</th>
   <td>{data.mobileNumber}</td>
   <td>{data.joiningDate}</td>
   <td>{data.dueDate}</td>
   
   <td>{data.subscription}</td>
    <td>{data.amount}</td> 
    <td>   <Button className="btn btn-primary" onClick={() => this.paymentSubmit(data)}>Payment</Button></td>
   <td>{data.status}</td>
   <td>   <Button className="btn btn-primary" onClick={() => this.editData(data)}>Edit</Button></td>
 </tr>:
  <tr className="table-info">
  <th  scope="row" className="text-primary" onClick={() => this.showData(data)}>{data.userName}</th>
  <td>{data.mobileNumber}</td>
  <td>{data.joiningDate}</td>
  <td>{data.dueDate}</td>
  
  <td>{data.subscription}</td>
   <td>{data.amount}</td> 
   <td>   <Button className="btn btn-primary" onClick={() => this.paymentSubmit(data)}>Payment</Button></td>
  <td>{data.status}</td>
  <td>   <Button className="btn btn-primary" onClick={() => this.editData(data)}>Edit</Button></td>
</tr>))}
  </tbody>
</table>
   </div>            
    </div>
                <Modal show={this.state.showModal} onHide={() => this.handleCloseShow()} size="lg">
          <Modal.Header closeButton>
            <Modal.Title>{this.state.sendData.userName} Details</Modal.Title>
          </Modal.Header>
          <Modal.Body>

            <Form className="myForm" >
              <Form.Group>
                <Form.Label> Branch Code <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="branchCode"
                  disabled={true}
                  value={this.state.sendData.branchCode}
                  // To override the attribute name
                  data-attribute-name="" />
              </Form.Group>
              <Form.Group>
                <Form.Label> User Name <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="userName"
                  value={this.state.sendData.userName}
                  disabled={true}
                  data-attribute-name="" />
             
              </Form.Group>

              <Form.Group>
                <Form.Label> Mobile Number <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="mobileNumber"
                  value={this.state.sendData.mobileNumber}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group>

              <Form.Group>
                <Form.Label> Joining Date <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="joiningDate"
                  value={this.state.sendData.joiningDate}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group>

              <Form.Group>
                <Form.Label> Paid Date <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="paidDate"
                  value={this.state.sendData.paidDate}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group>

              <Form.Group>
                <Form.Label> Due Date <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="dueDate"
                  value={this.state.sendData.dueDate}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group>

              <Form.Group>
                <Form.Label> Status <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="status"
                  value={this.state.sendData.status}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group>
              <Form.Group>
                <Form.Label> Subscription <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="subscription"
                  value={this.state.sendData.subscription}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group>
              <Form.Group>
                <Form.Label> Amount <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="amount"
                  value={this.state.sendData.amount}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group>
              
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={() => this.handleSubmitShow()}>
              Ok
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal show={this.state.showPaymentModal} onHide={() => this.handleClosePayment()} size="lg">
          <Modal.Header closeButton>
            <Modal.Title>{this.state.sendData.userName} Payment Submit</Modal.Title>
          </Modal.Header>
          <Modal.Body>

            <Form className="myForm" >
              {/* <Form.Group>
                <Form.Label> Branch Code <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="branchCode"
                  disabled={true}
                  value={this.state.sendData.branchCode}
                  // To override the attribute name
                  data-attribute-name="" />
              </Form.Group> */}
              <Form.Group>
                <Form.Label> User Name <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="userName"
                  value={this.state.sendData.userName}
                  disabled={true}
                  data-attribute-name="" />
             
              </Form.Group>

              <Form.Group>
                <Form.Label> Mobile Number <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="number"
                  name="mobileNumber"
                  value={this.state.sendData.mobileNumber}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group>

              {/* <Form.Group>
                <Form.Label> Joining Date <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="date"
                  name="joiningDate"
                  value={this.state.sendData.joiningDate}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group> */}

              {/* <Form.Group>
                <Form.Label> Payment Date <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="paidDate"
                  value={this.state.sendData.paidDate}
                  data-attribute-name="" />
              </Form.Group> */}

              {/* <Form.Group>
                <Form.Label> Payment Date <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="date"
                  name="dueDate"
                  value={this.state.sendData.dueDate}
                 
                  data-attribute-name="" />
              </Form.Group> */}

              {/* <Form.Group>
                <Form.Label> Status <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="text"
                  name="status"
                  value={this.state.sendData.status}
                  disabled={true}
                  data-attribute-name="" />
              </Form.Group> */}
              <Form.Group>
                    <Form.Label >Subscription <i className="text-danger">*</i></Form.Label>
                       { this.state.optionRadio.map((data,index)=>( <Form.Check  type="radio" key={index} className="p-1"
                          name="subscription"
                          onChange={this.form.handleChangeEvent} value={data}  id={data}
                          label={data} custom />))} 
                  </Form.Group>
              <Form.Group>
                <Form.Label> Amount <i className="text-danger">*</i> </Form.Label>
                <Form.Control
                  type="number"
                  name="amount"
                  // value={this.state.sendData.amount}
                  onChange={this.form.handleChangeEvent}
                  onBlur={this.form.handleBlurEvent}
                  data-attribute-name="" />
                   <div className="alert-danger text-danger">
                  {this.state.errors.amount
                  ? this.state.errors.amount
                  : ""}
                  </div>
              </Form.Group>
             
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={() => this.handleSubmitPayment()}>
              Submit
            </Button>
          </Modal.Footer>
        </Modal>

        </div>
     )}
     </React.Fragment>
     

   );
 }
}
export default Homepage;
